client.on('guildMemberAdd', guildMember =>{
    let welcomeRole = guildMember.guild.roles.cache.find(role => role.name === 'Member');
 
    guildMember.roles.add(welcomeRole);
    guildMember.guild.channels.cache.get('859419650893807626').send(`Welcome <@${guildMember.user.id}> to our server! Make sure to check out the rules channel!`)
});